import React from 'react'

const Navbar = () => {

    const links = [
        {
            id: 1,
            link: 'home'
        },
        {
            id: 2,
            link: 'movies/shows'
        },
        {
            id: 3,
            link: 'subscription'
        },
        {
            id: 4,
            link: 'profile'
        },
    ]
    return (
        <>



            <nav className='bg-cyan-500 flex justify-between items-center'>
                <h1 className='text-5xl text-white px-4 ' >Metflix</h1>
                <input type="text" placeholder='Search...' className=' w-96 p-3  rounded ' />


                <ul className=' hidden  md:flex  space-x-5 px-5 capitalize  py-5  '>
                    {links.map(({ id, link }) => {
                        return (<>
                            <li key={id} className='text-xl text-white font-mono '>{link}</li>
                        </>)
                    })}
                </ul>

                
      {
        nav && (
          <ul className="flex flex-col justify-center items-center absolute top-0 left-0 w-full h-screen bg-gradient-to-b from-black to-gray-800 text-gray-500">
            {links.map(({ id, link }) => (
              <li
                key={id}
                className="px-4 cursor-pointer capitalize py-6 text-4xl"
              >
                <Link
                  onClick={() => setNav(!nav)}
                  to={link}
                  smooth
                  duration={500}
                >
                  {link}
                </Link>
              </li>
            ))}
          </ul>
        )
      }






            </nav>



        </>
    );

}

export default Navbar
