import React, { useState } from 'react'
import Questions from "./Questions";
import { IoMdAdd } from "react-icons/io";
import { RxCross1 } from "react-icons/rx";


const FAQ = () => {


    const [data, setData] = useState(Questions);

    return (
        <>
            {
                data.map((currElem) => {
                    const { id } = currElem;
                    return <Doubt key={id}   {...currElem}/>
                })
            }
        </>
    )


    
}

export default FAQ



