import React from 'react'


const Navbar = () => {
    const [nav, setNav] = useState(false);

    const links = [
        {
            id: 1,
            link: 'home'
        },
        {
            id: 2,
            link: 'movies/shows'
        },
        {
            id: 3,
            link: 'subscription'
        },
        {
            id: 4,
            link: 'profile'
        },
    ]
    return (
        <>



            <nav className='bg-cyan-500 flex justify-between items-center'>
                <h1 className='text-5xl text-white px-4 ' >Metflix</h1>
                <input type="text" placeholder='Search...' className=' w-96 p-3  rounded ' />


                <ul className=' hidden  md:flex  space-x-5 px-5 capitalize  py-5  '>
                    {links.map(({ id, link }) => {
                        return (<>
                            <li key={id} className='text-xl text-white font-mono '>{link}</li>
                        </>)
                    })}
                </ul>

                <div onClick={() => { setNav(!nav) }} className='md:hidden ' >
                    { nav ? <button>Button</button> : <button>Cross</button> } </div>





            </nav>



        </>
    );

}

export default Navbar
