import React, { useState } from "react";
import { FaBars, FaTimes } from "react-icons/fa";

const Navbar = () => {
    const [nav, setNav] = useState(false);

    const links = [
        {
            id: 1,
            link: "home",
        },
        {
            id: 2,
            link: "movies/show",
        },
        {
            id: 3,
            link: "subscription",
        },
        {
            id: 4,
            link: "profile",
        },
       
    ];

    return (

        <div className="flex justify-between items-center w-full h-20 px-4 text-white bg-cyan-500 fixed">
            <div>

                <h1 className="text-5xl font-signature ml-2">
                    <a href="">Metflix</a>
                </h1>
            </div>



            <ul className="hidden md:flex">

            <input type="text"  className=" px-5 w-96 rounded h-10  " placeholder="Search..."/>
                {links.map(({ id, link }) => (
                    <li
                        key={id}
                        className="px-8  cursor-pointer capitalize font-medium text-white py-2 hover:scale-105 duration-200"
                    >
                        <li>
                            {link}
                        </li>

                    </li>
                ))}
            </ul>

            <div
                onClick={() => setNav(!nav)}
                className="cursor-pointer pr-4 z-10 text-white md:hidden"
            >
                {nav ? <FaTimes size={30} /> : <FaBars size={30} />}
            </div>

            {
                nav && (

                    <ul className="flex flex-col justify-center items-center absolute top-0 left-0 w-full h-screen bg-gradient-to-b from-black to-cyan-300 text-white">
                        {links.map(({ id, link }) => (
                            <li
                                key={id}
                                className="px-4 cursor-pointer capitalize py-6 text-4xl"
                            >
                                <li
                                    onClick={() => setNav(!nav)}
                                >
                                    {link}
                                </li>
                            </li>
                        ))}
                    </ul>
                )
            }
        </div >
    );
};

export default Navbar;
