import React, { useState } from 'react'
import Questions from "./Questions";
import { IoMdAdd } from "react-icons/io";
import { RxCross1 } from "react-icons/rx";
import Doubt from './Doubt';

const FAQ = () => {


    const [data, setData] = useState(Questions);

    return (
        <>
            <div>
                <div className='bg-black h-full'>

                    <h1 className='text-center text-white text-5xl font-bold py-10' > Frequently Asked Questions</h1>
                    {
                        data.map((currElem) => {
                            const { id } = currElem;
                            return <Doubt key={id}   {...currElem} />
                        })
                    }

                    <div className='px-20'>
                        <p className='text-white' >Ready to watch? Enter your email to create or restart your membership.</p>
                    </div>
                </div>
            </div>


        </>
    )



}

export default FAQ



