import React from 'react'
import tv from "../tv.png"

const Section2 = () => {

    const sections = [
        {
            heading: 'Enjoy on your TV',
            desc: ' Watch on smart TVs, PlayStation, Xbox, Chromecast, Apple TV, Blu-ray players and more.',
            src: tv
        },
    ]

    return (<>
        {sections.map(() => {
            return ( <div className='h-96 bg-black my-2 '>
            <div className='flex justify-around px-44'>
                <div>
                    <h1 className='text-white mt-20   font-bold text-6xl'></h1>
                    <p className='text-white mt-5 text-2xl w-1/2'></p>
                </div>
                <div>
                    <img src={tv} alt="" className='h-72' />
                </div>
            </div>
        </div>
 )
        })}
          </>
    )
}

export default Section2
