import React from 'react';
import poster from "../Images/netflix_poster.jpg";
import "../index.css"; // Import the CSS file for styling

const Home = () => {
    return (
        <div className="home">

            <div className="background-image text-white">
                <div className='flex  justify-around my-5'>
                    <h1 className='text-7xl  text-red-600 font-bold cursor-pointer'>METFLIX</h1>
                    <div className='flex space-x-10'>
                        <input type="text" placeholder='Search...' className='w- px-7  py-3 h-10 my-5 rounded' />
                        <button className='bg-red-600  h-10 px-5 my-5 rounded'>Sign In </button>
                    </div>
                </div>

                <div></div>
            </div>

            <img src={poster} alt="image"  />
        </div>
    );
}

export default Home;
