import React from 'react'
import Questions from "./Questions";
import {IoMdAdd} from "react-icons";

const FAQ = () => {
    return (
        <>
            <div className='bg-black h-full'>

                <h1 className='text-center text-white text-5xl font-bold py-10' > Frequently Asked Questions</h1>

                <div className=' mx-40'>

                    {
                        Questions.map(({ id, que, ans }) => {
                            return <>


                                <div className=' cursor cursor-pointer'>
                                    <div className='flex justify-between bg-gray-600 w-3/3  mt-2 h-20  '>
                                        <div className='text-white px-7 py-5 text-3xl ' key={id}>
                                            {que}</div>
                                            <div>
                                                
                                            </div>
                                    </div>
                                    
                                    
                                    <div className='flex justify-between bg-gray-600 w-3/3  mt-2 h-full '>
                                        <div className='text-white px-7 py-5 text-3xl ' key={id}>
                                            {ans}</div>
                                    </div>


                                </div>

                            </>
                        })
                    }

                </div>

            </div>
        </>
    )
}

export default FAQ
